tutorialsIndex_es = {"Basico", "Intermedio", "Avanzado", "Profeciones", "Habilidades", "Extra", "Torneos", "Hot Quests"}

tutorialsInfo_es = {
    {
        {name = "01. Introducion", text = "1. El Menu Pokemon es donde llama a su Pokemon,revisa su estado y lo comanda.\n\n2. Su mochila es donde usted va a guardar sus objetos y Pokemon.\n\n3. Informacion de su personaje, como nivel y experiencia.", img = "imgs/basic/b1"}, 
        {name = "02. Controlar Objetos", text = "Para controlar objetos, solo haga click y mantenga precionado el boton izquierdo de su mouse y arrastrelo para donde quiera.", img = "imgs/basic/b2"}, 
        {name = "03. Llamar Pokemon", text = "Para llamar a su Pokemon, arrastrelo para el menu Pokemon,despues haga clic con el boton derecho de su raton sobre el.", img = "imgs/basic/b3"}, 
        {name = "04. Atacando Enemigos", text = "Para atacar un enemigo,solo haga click en su objetico (boton derecho del mouse),despues que el objetivo este marcado,su Pokemon atacara al enemigo automaticamente.", img = "imgs/basic/b4"},
        {name = "05. Usando Ataques", text = "Los ataques de cada Pokemon son desbloqueados conforme usted sube de nivel. Para usar un ataque solo haga click en su icono (boton izquierdo del raton) o utilize atajos (F1,F2,F3,F4,...) Los atajos tambien pueden ser redefinidos en las configuracions de Hotkeys.", img = "imgs/basic/b5"},
        {name = "06. Loot del Pokemon", text = "Todo Pokemon despues de ser derrotado puede contener objetos. Para recolectarlos solo haga click en el pokemon derrotado (boton derecho del mouse). Si la opcion de Auto-Loot estuviera habilitada los objetos seran recolectados automaticamente, en caso contrario usted debera arrastrarlos hasta su mochila.", img = "imgs/basic/b6"}, 
        {name = "07. Capturar Pokemon", text = "Para capturar un Pokemon, haga click en sus pokebolas vacias (boton derecho del mouse) y despues use en un pokemon derrotado (boton izquierdo del mouse). ", img = "imgs/basic/b7"}, 
        {name = "08. Mapa", text = "El mapa del juego puede ser abierto con el atajo (Ctrl+M) o por su icono. ", img = "imgs/basic/b10"},
		{name = "09. Tiendas", text = "Para abrir una tienda o basta con hablar con el npc decir (hi/trade/yes) ", img = "imgs/basic/b11"},        
        {name = "10. Pokedex", text = "Para desbloquear datos de un nuevo Pokemon basta hacer click sobre el icono de la Pokedex y despues usarla en el Pokemon que desea recolectar datos (boton izquierdo del mouse).", img = "imgs/basic/b12"},
        {name = "11. Deposito de Objetos", text = "El deposito puede ser encontrado en cualquier Centro Pokemon y para abrirlo haga click en el con el boton derecho del mouse. En el deposito ustedes puede guardar cuantos Pokemons y Objetos quieras.", img = "imgs/basic/b13"},
        {name = "12. Escaleras y Cuerdas", text = "Para subir escaleras o cuerdas solo haga click con el boton derecho de su mouse en la base de la escalera o cuerda como esta indicando en la imagen.", img = "imgs/basic/b9"},
        {name = "13. Curar Pokemon", text = "Para curar a sus Pokemon heridos, basta ir hasta un Centro Pokemon y decir (hi) a la Nurse Joy. Curar sus Pokemon no tiene ningun costo.", img = "imgs/basic/b8"},
    },
    {
        {name = "01. Ver Objetos", text = "Para ver algo posicione el raton y presione los 2 botones al mismo tiempo.", img = "imgs/inter/a1"},
        {name = "02. Cambiar de Ropa", text = "Para cambiar su ropa mantenga presionada la tecla (Ctrl) y haga click en su personaje con el boton derecho del raton despues selecciones (Fijar Outfit).", img = "imgs/inter/a2"},
        {name = "03. Pesca", text = "Para pescar vaya a la pantalla superior al lado > del icono Pokedex y haga Click (Derecho o Izquierdo) y coloquelo en el Agua.", img = "imgs/inter/a3"},
        {name = "04. Felicidad de el Pokemon", text = "Con el comando !love usted mejora el estado de animo de su pokemons (Puede Ser Usado Cuantas Veces Quieras).", img = "imgs/inter/a4"},
        {name = "05. Instancia de Batalla", text = "Las instancias de batalla son muy importantes, principalmente para duelos.\n\nOfensiva: 10% de ataque extra, sin embargo 10% de defensa perdida.\n\nBalanceado: Fuerza Natural del Pokemon.\n\nDefensiva: 10% de defensa extra, sin embargo 10% de ataque perdido.", img = "imgs/inter/a5"},
        {name = "06. Hotkeys", text = "Aqui usted puede añadir nuevos atajos y hasta colocar objetos como revive, medicine y potions o comandos de batalla con movimientos del Pokemon, Ataques.", img = "imgs/inter/a6"},
        {name = "07. Aceptando Quests", text = "Para Aceptar una Quest o Mision es solo hablar con el Npc (Hi/help/yes).", img = "imgs/inter/a7"},
        {name = "08. Quest Diaria", text = "Las Quests o Misiones Diarias Estan Ubicadas en Tc >> Npcs\n\n(Leiko: Nivel Iniciante)\n\n(Miyuki: Nivel Facil)\n\n(Amaya: Nivel Medio)\n\n(Junko: Nivel Dificil).", img = "imgs/inter/a8"},
        {name = "09. Batallando con Npc", text = "Es solo decir (hi/battle/yes).", img = "imgs/inter/a9"},
        {name = "10. Viajando", text = "Para Viajar con Npc´s es muy facil, solo diga el dialogo (hi/travel/yes).", img = "imgs/inter/a10"},
        {name = "11. Evolucionar Pokemon", text = "Para evolucionar un Pokemon basta usar una o mas piedras de evolucion en el. ver cual es la piedra necesaria en la Pokedex (Psychic=Enigma Stone) y Asi Sucesivamente.", img = "imgs/inter/a11"},
        {name = "12. Lista de Amigos", text = "La lista de amigos muestra si sus amigos estan Online o Offline en el juego, mas alla de proporcionar varias opciones como mensajes privados o ignorar. adicionar a un amigo es muy facil (Ctrol+Click Derecho) en la Friend List y Add New Vip.", img = "imgs/inter/a12"},
    },	
    {
        {name = "01. Trade", text = "Para hacer un cambio, mantenga apretado (Ctrol) y haga click con el boton derecho del mouse en el objeto que desea cambiar y seleccione (Trade With) despues usa en el jugador que usted desea hacer el cambio. (en caso de varios objetos, coloquelos en una bag).", img = "imgs/advanced/c1"},
        {name = "02. Casas", text = "Para comprar una casa, usted necesita tener cuenta (ViP) y por lo menos ser nivel 30.\n\nPrimeramente, encuentre una casa sin dueño, (Vea) la puerta de la casa el precio, despues este frente de la puerta de la casa y diga !buyhouse\n\n Cada mes sera cobrado el precio de la casa en su deposito, de lo contrario, perdera su casa.", img = "imgs/advanced/c2"},
        {name = "03. Duelo", text = "Click el Boton Battle en la parte superior de la pantalla y luego seleccione al otro jugador esta el sistema de\n\n1 Vs 1\n\n2 Vs 2\n\n3 Vs 3\n\n4 Vs 4", img = "imgs/advanced/c3"},
        {name = "04. Channels", text = "1.Private Chat Channel (para hacer un canal particular e invitar jugadores).\n\n2.Game-Chats (Sala de conversacion entre los jugadores de todo el servidor).\n\n3.Trade Center (sala de comercio, para comprar y vender objetos y Pokemon).\n\n4.Help (Sala de ayuda, para preguntas a los tutores).", img = "imgs/advanced/c4"},
        {name = "05. Guild", text = "Una Guild solo puede ser creada por un jugador nivel 80 o mas y necesita 2 Vice-Lider. Para crear o entrar en una guild es muy simple solo haga click en el boton guild y seleccione y siga la opcion deseada.", img = "imgs/advanced/c5"},
        {name = "06. Television", text = "Cada jugador puede abrir su propio canal en vivo, basta tener una camara de TV que puede ser comprada con el Npc Mite.\n\nPara asistir un canal, solo haga click en una Tv y seleccione el canal deseado.\n\ncomandos basicos para los dueños del canal: !listtv y !kicktv player", img = "imgs/advanced/c6"},
        {name = "07. Direccionar Pokemon", text = "Para Cambiar la direccion de un Pokemon es muy simple y util para duelos y hunts. Solo diga o coloque en sus hotkeys los siguientes comandos:\n\nNorte:t1\n\nEste:t2\n\nSur:t3\n\nOeste:t4", img = "imgs/advanced/c7"},
        {name = "08. Clanes", text = "A Partir del momento en que el entrenador alcance el nivel 80 el pasa a ser aceptado en los Clanes Pokemon, divididos de acuerdo con los elementos, donde comenzara en el rank 1 y podra llegar hasta del rank 5, siguiendo la jerarquia de los Clanes.\n\nPara subir de rank dentro de su Clan usted tendra que seguir las instrucciones de sus mentores, que cambiaran a cada rank que usted suba. para ingresar en un clan, hable con el lider que esta en el salon de cada clan, dentro del Tc.\n\nTodo Clan posee bonus de ataque y defensa sobre sus elementos, mas alla de tener un Pokemon exclusivo donde puede ser adquirido por un Npc del Clan:\n\nNaturia: Grass/Bug - Pokemon: Shiny Vileplume.\n\nVolcanic: Fire - Pokemon: Shiny Flareon.\n\nRaibolt: Electric - Pokemon: Shiny Jolteon.\n\nSeavell: Water/Ice - Pokemon: Shiny Vaporeon.\n\nWingeon: Dragon/Flying - Pokemon: Shiny Fearow.\n\nOrebound: Rock/Ground - Pokemon: Shiny Golem.\n\nPsycraft: Psychic/Fairy - Pokemon: Shiny Hypno.\n\nMalefic: Ghost/Poison/Dark - Pokemon: Shiny Nidoking.\n\nIronhard: Metal/Crystal - Pokemon: Shiny Lucario", img = "imgs/advanced/c9"},
        {name = "09. Cambiando Tokens", text = "Para cambiar tokens no es necesario ningun requerimiento vaya hasta Shamouti, en el salon de duelist Brotherhood que esta al oeste del Centro Pokemon\n\nUse el (Held Item Factory) y seleccione los Tokens que desea cambiar.", img = "imgs/advanced/c8"},
        {name = "10. Party Channel", text = "Para Hacer Party con una persona basta dar (Ctrol+Click) en el otro jugador y click en (Invitar a Party).", img = "imgs/advanced/c10"},
    },	
    {
        {name = "01. Estilista", text = "Teniendo a Shae como mentora, estilista es la profesion que mas tiene posibilidades, haga outfits, addons, muebles, juguetes y hasta accesorios utiles para el dia a dia.\n\nUn estilista adquiere creatividad, estudiando y recolectando ideas de pokemones derrotados durante el dia, que puede usarlos hasta para mejorar items hechos por otras profesiones.\n\nComo exclusividad, un estilista puede construir accesorios de nadar, snowboard y sandboard. lo que deja a esta profesion una de las mas deseadas!\n\nTambien es posible cortar arboles para contruir muebles y pisos, creando varias posibilidades de mercado!", img = "imgs/profesion/p1"},
        {name = "02. Ingeniero", text = "Ingenieros son especialistas en el arte de la construccion de objetos mecanicos y electronicos, desde un simple televisor hasta un gigante robot.\n\nEnseñando artes antiguas, el mentor de los ingenieros es Kurt, es notado principalmente por su trabajo con la creacion de pokebolas personalizadas, con capacidades unicas para capturar pokemones especificos.\n\nUsando minerales que pueden ser encontrados en todo el mundo, este profesional consigue crear multiples accesorios y items que facilitan la vida de cualquier entrenador, sea en batalla o fuera de ella.", img = "imgs/profesion/p2"},
        {name = "03. Proffesor", text = "Profesor es sin duda una profesion muy lucrativa. El mentor de los profesores es el Profesor Elm de la ciudad Newbark en el continente Johto.\n\nLa principal responsabilidad de un profesor es administrar y guiar a sus alumnos en su jornada y aventura, con la vision de dejarlos mas fuertes y con mas experiencia.\n\nLos Profesores tambien son maestros en artes del jardin, cultivando bayas y plantas, cada una con un efecto diferente.\n\nCon el dominio de la alquimia, estos profesionales tambien pueden construir materia, un compuesto usado para elevar el boost de un pokemon que ya esta +50.", img = "imgs/profesion/p3"},
        {name = "04. Adventurer", text = "Un aventurero no quiere quedarse parado, el quiere la adrenalina de explorar y aventurarse, encontrando sorpresas por el camino.\n\nTodd Snap, un famoso fotografo que viaja por el mundo para perseguir sus sueños y mitos es el mentor de esta profesion.\n\nDiferente de las demas, la rutina del aventurero no es exacta todo es un misterio, sigue tu radar, mapas y pistas para encontrar baules de tesoros o lugares inexplorados!", img = "imgs/profesion/p4"},
    },	
    {
        {name = "01. Mover", text = "Para mover a su Pokemon, abra la ventana Pokemon y haga click en el icono (Ordenar) y despues haga click en el suelo, su Pokemon ira hasta el lugar escogido.\n\nEste comando es de vitar importancia, en duelos de alto nivel por eso recomendado utilizar un hotkey para eso. El hotkey predeterminado de ordenar.", img = "imgs/habilitys/f1"},
        {name = "02. Corte", text = "Para cortar arbustos, solo ordene a un Pokemon con la habilidar cut en el arbusto.", img = "imgs/habilitys/f2"}, 
        {name = "04. Dig", text = "Para Abrir agujeros cerrados, solo ordene a un pokemon con la habilidad Dig en el agujero cerrado.", img = "imgs/habilitys/f3"},
        {name = "05. Light", text = "Para usar luz, solo ordene a un Pokemon con la habilidad Light en si mismo.", img = "imgs/habilitys/f4"},
        {name = "06. Rock Smash", text = "Para quebar piedras, solo ordene a un Pokemon con la habilidad Rock Smash en la piedra.", img = "imgs/habilitys/f5"},
        {name = "07. Headbutt", text = "Solo ordene a un Pokemon con la habilidad Headbutt en arboles para encontrar Pokemon Salvajes.", img = "imgs/habilitys/f6"},
        {name = "08. Ride", text = "Para montar en un Pokemon, solo ordene un Pokemon con ride en su entrenador.\n\nPara desmontar repita el mismo proceso.", img = "imgs/habilitys/f7"},
        {name = "09. Surf", text = "Para nadar, solo ordene un Pokemon con Surf, en la orilla de algun lugar con agua, despues camine hasta su Pokemon.\n\nPara salir, repita el mismo proceso.", img = "imgs/habilitys/f8"},
        {name = "10. Fly", text = "Para volar en un Pokemon, solo ordene a un Pokemon con Fly en su entrenador.\n\nPara cambiar la altura, use los comandos (h1) para subir y (h2) para bajar.\n\nPara desmontar, repita el mismo proceso.", img = "imgs/habilitys/f9"},
        {name = "11. Teleport", text = "Para teletransportarse para alguna ciudad, usted necesita de un Pokemon con Teleport. y digitar el comando (h`cerulean o nombre de la ciudad).", img = "imgs/habilitys/f11"},
        {name = "12. Blink", text = "Cuando un Pokemon tiene Blink y usted le ordena que vaya hasta algun lugar, el automaticamente usara el blink.", img = "imgs/habilitys/f10"},
    },
    {
        {name = "01. Saffari Zone", text = "La Zona Saffari esta localizada en Fuchsia a la izquierda del Centro Pokemon. Es un lugar donde usted podra encontrar raros Pokemon. Para entrar al Saffari usted debe tener cuenta ViP y Pagar 1000DL. El Dialogo es (hi/Saffari/Yes).", img = "imgs/extra/m1"},
        {name = "02. Golden Arena", text = "La Golden Surviva Arena es un evento que ocurre cada 5 horas, donde los jugadores son teletransportados para una arena y deben sobrevivir a las waves con varios pokemon y cada vez mas fuertes.\n\nPara participar en este evento usted debe tener un Meowth Coin este item puede ser comprado en el Npc Meowth Dealer.", img = "imgs/extra/m2"},
        {name = "03. Outland", text = "Outland es un mundo paralelo, donde los Pokemon salvajes son muy poderosos. Solo jugadores nivel 150 o mas pueden entrar a esta area. Existen 3 Portales para outland: Norte, Sur y Este.", img = "imgs/extra/m3"}, 
        {name = "04. Pokemon Ditto", text = "Ditto es un Pokemon especial que no tiene requerimiento de nivel y que puede ser adquirido en el Diamond Shop. la habilidad especial de Ditto es Transform, que hace como que el ditto pueda transformarse en otro Pokemon.\n\nPara transformar a Ditto, usted debe usar el boton ordenar en otro Pokemon. En caso que quiera revertir la transformacio, use !revert.", img = "imgs/extra/m4"},
        {name = "05. Boost", text = "Boost es un metodo de dejar su Pokemon mas fuerte. Para eso tiene que ir a las Boost Machine en Coliseum (Gary`s Lab) <<<\n\nUsted Debe colocar en la boost machine como la imagen. Stone < y Pokemon >", img = "imgs/extra/m5"},
        {name = "06. Nombre del Pokemon", text = "El Nombre del Pokemon puede ser definido si el tiene un Boost Igual o Superior a 5.\n\nPara Cambiar el nombre, vaya hasta la Npc Shurya en Gary`s Lab o en el Tc y diga el Siguiente dialogo:\n\n(hi/nick/yes/Nombre deseado).", img = "imgs/extra/m6"},
        {name = "07. Aura del Pokemon", text = "Aura es una Animacion para cuando el Pokemon Alcance Boost 50.\n\nEste Puede Ser Obtenido en el Gary`s Lab o Tc con el Npc Shurya (Hi/aura/yes/Nombre del aura).", img = "imgs/extra/m7"},
        {name = "08. Addons", text = "Addons son ropas para Pokemon. Usted puede encontrarlas en el Diamond Shop o en Las Profesiones.", img = "imgs/extra/m8"},
        {name = "09. Held Items", text = "El Held Item es un item que un Pokemon puede cargar. Cada uno viene con una caracteristica denominada Tier, que no es mas que su nivel, o sea, cuanto mayor sea el Tier de un Held Item mejor el sera y mas raro.\n\nSu Pokemon puede cargar solamente dos Held Item por vez (Uno con prefijo (X) y otro con Prefijo (Y). Es Posible hacer la sustitucion del Held Item en su Pokemon, mas usted perdera aquel que fue sustituido.\n\nLista de Held Item X:\n\nX-Poison    Tier 1 a Tier 7\n\nX-Hellfire  Tier 1 a Tier 7\n\nX-Attack    Tier 1 a Tier 7\n\nX-Return    Tier 1 a Tier 7\n\nX-Defense   Tier 1 a Tier 7\n\nX-Boost     Tier 1 a Tier 7\n\nLista de Held Item Y:\n\nY-Regeneration Tier 1 a Tier 7\n\nY-Cure         Tier 1 a Tier 7", img = "imgs/extra/m9"},  
		{name = "10. Taunt", text = "Taunt es una animacion que muchas outfits pueden utilizar. La mayoria de los outfits compradas en la diamond shop tienen taunt. Algunos outfits comunes del juego tambien tienen taunt, sin embargo deben ser desbloqueados con misiones.\n\nEl comando para usar taunt es !taunt", img = "imgs/extra/m10"},
		},		
    {
        {name = "01. Torneo", text = "El torneo es realizado en el Coliseum, que esta localizado a la izquierda del centro pokemon de la ciudad de viridian. Los torneos suceden cada sabado y domingo, cambiando sus Modalidades cada semana.\n\nHorario: No definido.", img = "imgs/tournament/g1"},
    },	
    {
        {name = "01. Cofres Perdidos",      text = "Nivel Minimo: 30 a 40.\n\nSe rumorea que hay 4 cofres alrededor del mapa que guardan grandes recompesas a los aventureros valientes que deciden explorar el mundo Pokemon.\n\nEl primero se encuentra en la region de Cerulean, al Oeste de la ciudad, protegida por Pokemon Venenosos.\n\nEl Segundo esta en la region de Pewter, al noreste del Centro Pokemon con muchos Pokemon de tipo piedra, tierra y luchador protegiendo el tesoro.\n\nEl tercero se encuentra al este de la ciudad abandonada de Lavender, con varios Pokemon de tipo Psychic protegiendolo.\n\nEl ultimo esta en la isla principal de Seafom, en el subsuelo, junto a varios pokemon de tipo hielo y agua.", img = "imgs/hotquest/t1"},
        {name = "02. Team Rocket y Police", text = "Nivel Minimo: 40.\n\n¿Chico bueno o villano? Llego la hora de que usted decida que camino seguir en el mundo Pokemon. Piense muy bien, pues esa eleccion no tiene vuelta atras y determinara su futuro. Numerosas quests y desafios seran habilitados despues de su eleccion, tal vez sea temprano para completarlos todavia, mas no se rinda, luche y gane la confianza de Giovanni (Rocket/Celadon) o de Captain Jenny (Police/Pallet).", img = "imgs/hotquest/t2"},
        {name = "03. Profesor Oak",         text = "Nivel Minimo: 50.\n\nEl Profesor Oak necesita de usted para una serie de tareas, ayudelo para recibir recompensas increibles.", img = "imgs/hotquest/t3"},
        {name = "04. Gary Oak",             text = "Nivel Minimo: 50.\n\nGary Oak se ha convertido en un gran investigador Pokemon,ayudelo en su busqueda y obtenga buenas recompensas.", img = "imgs/hotquest/t4"},
        {name = "05. Ash Ketchum",          text = "Nivel Minimo: 50.\n\nAsh Ketchum necesita de su ayuda para investigar al Team Rocket y otros misterios que estan ocurriendo en el mundo! Mas primero, pruebe que usted es digno de su confianza.", img = "imgs/hotquest/t5"},
        {name = "06. Elite Four Bruno",     text = "Nivel Minimo: 60.\n\nBruno es un miembro de la Elite Four, Los Pokemon luchadores son su especialidad y su vida es entrenarlos. Recientemente Bruno esta ayudando a una pequeña niña, el pretende estudiar un raro Pokemon de Crystal para encontrar inspiracion para Mateo, el hermano de Marissa.", img = "imgs/hotquest/t6"},
        {name = "07. Elite Four Lorelei",   text = "Nivel Minimo: 60.\n\nLorelei, miembro de la Elite Four, es conocida por su estilo logico y calculador en la batalla, Pokemon de hielo son su especialidad. Recientemente ella busca refuerzos para conseguir varios items.", img = "imgs/hotquest/t7"},
        {name = "08. Elite Four Agatha",    text = "Nivel Minimo: 100.\n\nAgatha es una señora con un temperamento peculiar y mas un miembro de la Elite Four. Su especialidad son los Pokemon fantasma pero siente una enorme curiosidad por cualquier Pokemon con caracteristicas horripilantes. Su Casa fue enviada para otra dimension Por Dark Abra, algunos espejos pueden abrir el pasaje para esa dimension, encuentrelos y ayude a Agatha.", img = "imgs/hotquest/t8"},
        {name = "09. Elite Four Lance",     text = "Nivel Minimo: 80.\n\n Lance es el lider de la Elite Four, su especialidad son los Pokemon Dragon, es un gran desafio para el entrenar y enseñar tecnicas para ese tipo peculiar de Pokemon Recientemente el estuvo estudiando un artefacto peculiar: El Charizard Heart, poco se sabe sobre este artefacto, pero se cree que se podria encontrar en un lugar con muchos Charizard.", img = "imgs/hotquest/t9"},
        {name = "10. Bill Lighhouse",       text = "Nivel Minimo: 80.\n\nPara resolver el misterio usted debe encontrarse con el invertigador Bill que vive cerca de Cerulean. Mas cuidado, ese no es un camino facil, usted con seguridad tendra que cumplir diversos desafios por lo cual tal vez no este preparado, buena suerte en su viaje.", img = "imgs/hotquest/t10"},
        {name = "11. Cyber World",          text = "Nivel Minimo: 120.\n\nEl misterioso Porygon siempre fue un enigma para los investigadores Pokemon, mas ahora los entrnadores descubrieron un medio para invadir el Cyber World! Para entrar en este mundo usted debe hablar con el Npc (Shadow) ubicado en Tc.", img = "imgs/hotquest/t11"},
        {name = "12. Batalla Final",        text = "Nivel Minimo: 120.\n\nFinalmente llego la hora de probar si usted es un Police o un Rocket de Elite. Invade la guarida de Police/Rocket y Ganale al lider de la faccion rival. No Olvide que este es el ultimo nivel del desafio de su Equipo, asi que este bien preparado, usted podra llevar a otros dos entrenadores de su equipo para tratar de completar este desafio, buena suerte.", img = "imgs/hotquest/t12"},
        {name = "13. Pokemon League",       text = "Nivel Minimo: 150.\n\nLa Liga Pokemon, uno de los principales objetivos de la mayoria de los entrenadores Pokemon por el mundo, consiste en 8 lideres de Gym de ciudades de Kanto, que le daran una insignia al ser derrotados. al conseguir las 8 insignias, la Elite Four de Kanto lo juzgara si usted es apto de entrar a la Pokemon League, donde usted batallara con los mejores! Pruebe que es capaz derrotandolos para ser el nuevo campeon de la Liga Pokemon.", img = "imgs/hotquest/t13"},
        {name = "14. Outland Main",         text = "Nivel Minimo: 150.\n\nPokemon muy fuertes y agresivos de otra dimension llamada Outland estan intentando invadir nuestro mundo, Lideres de Gym y entrenadores legendarios estan alli,¿que esta esperando para ayudar? Hable con Ash Ketchum en Outland East.", img = "imgs/hotquest/t14"},
        {name = "15. Duelist Brotherhood",  text = "Nivel Minimo: 150.\n\nLa Liga de los duelistas estan buscando entrenadores Pokemon fuertes lo bastante para encontrar Hunters,Npcs de Alta Gama Usted Debera derrotar (10.000) no hay limite de Npcs Por Dia.", img = "imgs/hotquest/t15"},
        {name = "16. Moro Island",          text = "Nivel Minimo: 100.\n\nLa principal atraccion del museo de arte de Moro Island, un trofeo de 300 Años de la isla naranja, fue robado. Se sospecha del Equipo Rocket, pero no se esta seguro. ¿Podra usted ayudar a la oficial jenny a solucionar este caso?", img = "imgs/hotquest/t16"},
        {name = "17. Golden Island",        text = "Nivel Minimo: 100.\n\nLa Tribu de Meowth de la Recompensa esta en la busqueda de un entrenador Pokemon que pueda ayudarlos a realizar su profecia, se rumorea que esta tribu suele premiar a los forasteros que los ayudan muy bien!.", img = "imgs/hotquest/t17"},
        {name = "18. Tarroco Island",       text = "Nivel Minimo: 60.\n\nEl Capitan de Policia Locker esta en Tarroco Island y necesita de fuertes policias para un ataque a la base Rocket.", img = "imgs/hotquest/t18"},
        {name = "19. Ascorbia Island",      text = "Nivel Minimo: 60.\n\nCassidy y Butch estan en Ascorbia Island, planeando un ataque contra los Police, ayudelos si usted es Rocket!.", img = "imgs/hotquest/t19"},
        {name = "20. Firefighter",          text = "Nivel Minimo: 30.\n\nEntrene el cuerpo de bomberos, sorprenda al capitan Aiden y a sus Pokemon acuaticos para conseguir una recompensa unica.", img = "imgs/hotquest/t20"},
        {name = "21. Grapefruit Island",    text = "Nivel Minimo: 60.\n\nUn Snorlax gigante esta devorando todas las grapefruit de las Seven Grapefruit Islands, ayude a la Investigadora Pokemon Ruby y a sus colegas biologos a impedir que todas las frutas sean devoradas!.", img = "imgs/hotquest/t21"},
        {name = "22. Kabuto Fossil Island", text = "Nivel Minimo: 100.\n\nHay Alguien saboteando la expedicion cientifica atras de Kabuto Fossil, encuentra a esa persona y ayude a la Nurse Joy a solucionar el misterio detras de es esta isla!.", img = "imgs/hotquest/t25"},
        {name = "23. Pikan Island",         text = "Nivel Minimo: 60.\n\nPikan Fruit es una fruta comun en esta isla, Pokemon que consumieron esta fruta por mucho tiempo se convirtieron en Pokemons Rosados. El Team Rocket esta intentando robar esos Pokemon, si usted es Rocket tendras que ayudarlos, si es police, intente impedirlos!.", img = "imgs/hotquest/t22"},
        {name = "24. Mew Island",           text = "Nivel Minimo: 120.\n\nMewtwo esta intentando dominar el mundo con su ejercito de clones, intenten impedirlo antes de que sea demasiado tarde. Esta mision puede ser repetida cada mes.", img = "imgs/hotquest/t23"},
        {name = "25. Mewtwo Final",         text = "Nivel Minimo: 200.\n\nMewtwo invito solo a los mejores entrenadores del mundo para presenciar su poder y la superioridad de los Pokemon clones.¿Sera que usted pueda conseguir detenerlo?.", img = "imgs/hotquest/t24"},
    },	    
}